const FRAME = "FCL_IFRAME"

const FRAME_STYLES = `
  position:fixed;
  top: 0px;
  right: 0px;
  bottom: 0px;
  left: 0px;
  height: 100vh;
  width: 100vw;
  display:block;
  background:rgba(0,0,0,0.25);
  z-index: 2147483647;
  box-sizing: border-box;
`

export function renderFrame(src) {
  invariant(
    !document.getElementById(FRAME),
    "Attempt at triggering multiple Frames",
    {src}
  )

  const $frame = document.createElement("iframe")
  $frame.src = src
  $frame.id = FRAME
  $frame.allow = "usb *; hid *"
  $frame.frameBorder = "0"
  $frame.style.cssText = FRAME_STYLES
  document.body.append($frame)

  const unmount = () => {
    if (document.getElementById(FRAME)) {
      document.getElementById(FRAME).remove()
    }
  }

  return [$frame, unmount]
}
import {renderFrame} from "./render-frame"
import {serviceEndpoint} from "./service-endpoint"

const CLOSE_EVENT = "FCL:FRAME:CLOSE"
const READY_EVENT = "FCL:FRAME:READY"
const RESPONSE_EVENT = "FCL:FRAME:RESPONSE"
const OPEN_EVENT = "FCL:FRAME:OPEN"

const noop = () => {}

const IGNORE = new Set([
  "monetizationstart",
  "monetizationpending",
  "monetizationprogress",
  "monetizationstop",
])

export function frame(service, opts = {}) {
  if (service == null) return {send: noop, close: noop}

  var tab = null
  const onClose = opts.onClose || noop
  const onMessage = opts.onMessage || noop
  const onReady = opts.onReady || noop
  const onResponse = opts.onResponse || noop

  window.addEventListener("message", internal)
  const [$frame, unmount] = renderFrame(serviceEndpoint(service))
  return {send, close}

  function internal(e) {
    try {
      if (typeof e.data !== "object") return
      if (IGNORE.has(e.data.type)) return
      if (e.data.type === CLOSE_EVENT) close()
      if (e.data.type === READY_EVENT) onReady(e, {send, close})
      if (e.data.type === RESPONSE_EVENT) onResponse(e, {send, close})
      onMessage(e, {send, close})

      // Backwards Compatible
      if (e.data.type === "FCL::CHALLENGE::RESPONSE") {
        onResponse(e, {send, close})
      }
      if (e.data.type === "FCL::AUTHZ_READY") onReady(e, {send, close})
      if (e.data.type === "FCL::CHALLENGE::CANCEL") close()
      if (e.data.type === "FCL::CANCEL") close()
    } catch (error) {
      console.error("Frame Callback Error", error)
      close()
    }
  }

  function close() {
    try {
      window.removeEventListener("message", internal)
      if (tab != null) tab.close()
      unmount()
      onClose()
    } catch (error) {
      console.error("Frame Close Error", error)
    }
  }

  function send(msg) {
    try {
      $frame.contentWindow.postMessage(
        JSON.parse(JSON.stringify(msg || {})),
        "*"
      )
    } catch (error) {
      console.error("Frame Send Error", msg, error)
    }
  }

  function open(msg) {
    try {
      tab = window.open(msg.endpoint)
      tab.focus()
    } catch (error) {
      console.error("Frame Open Error", msg, error)
    }
  }
}
import {normalizePollingResponse} from "../../../normalize/polling-response"
import {serviceEndpoint} from "./service-endpoint"
import {invariant} from "@onflow/util-invariant"
import {fetchService} from "./fetch-service"

const OPTIONS = {
  "HTTP/GET": "GET",
  "HTTP/POST": "POST",
}

const serviceMethod = service => {
  invariant(
    OPTIONS[service.method],
    "Invalid Service Method for type back-channel-rpc",
    {service}
  )
  return OPTIONS[service.method]
}

const serviceBody = service => {
  if (service.method === "HTTP/GET") return undefined
  if (service.method === "HTTP/POST" && service.data != null)
    return JSON.stringify(service.data)
  return undefined
}

export async function poll(service, canContinue = () => true) {
  invariant(service, "Missing Polling Service", {service})
  if (!canContinue()) throw new Error("Externally Halted")

  const resp = await fetchService(service, {
    method: serviceMethod(service),
  }).then(normalizePollingResponse)

  switch (resp.status) {
    case "APPROVED":
      return resp.data
    case "DECLINED":
      throw new Error(`Declined: ${resp.reason || "No reason supplied."}`)
    default:
      await new Promise(r => setTimeout(r, 500))
      return poll(resp.updates, canContinue)
  }
}
import {fetchService} from "./utils/fetch-service"
import {serviceEndpoint} from "./utils/service-endpoint"
import {normalizePollingResponse} from "../../normalize/polling-response"
import {frame} from "./utils/frame"
import {poll} from "./utils/poll"

export async function execHttpPost(service, signable, opts) {
  signable.data = service.data
  const resp = await fetchService(service, {
    data: signable,
  }).then(normalizePollingResponse)

  if (resp.status === "APPROVED") {
    return resp.data
  } else if (resp.status === "DECLINED") {
    throw new Error(`Declined: ${resp.reason || "No reason supplied."}`)
  } else if (resp.status === "PENDING") {
    var canContinue = true
    const {close: closeFrame} = frame(resp.local, {
      onClose() {
        canContinue = false
      },
    })

    return poll(resp.updates, () => canContinue)
      .then(serviceResponse => {
        closeFrame()
        return serviceResponse
      })
      .catch(error => {
        console.error(error)
        closeFrame()
        throw error
      })
  } else {
    console.error(`Auto Decline: Invalid Response`, {service, resp})
    throw new Error(`Auto Decline: Invalid Response`)
  }
}
import {execHttpPost} from "./strategies/http-post"
import {execIframeRPC} from "./strategies/iframe-rpc"

const STRATEGIES = {
  "HTTP/RPC": execHttpPost,
  "HTTP/POST": execHttpPost,
  "IFRAME/RPC": execIframeRPC,
}

export async function execService(service, msg, opts = {}) {
  try {
    return STRATEGIES[service.method](service, msg, opts)
  } catch (error) {
    console.error("execService(service, msg)", error, {service, msg, opts})
    throw error
  }
}
import {uid} from "@onflow/util-uid"
import {frame} from "./utils/frame"
import {normalizePollingResponse} from "../../normalize/polling-response"

export function execIframeRPC(service, body, opts) {
  return new Promise((resolve, reject) => {
    const id = uid()
    const includeOlderJsonRpcCall = opts.includeOlderJsonRpcCall

    body.data = service.data

    frame(service, {
      onReady(_, {send}) {
        try {
          send({
            type: "FCL:FRAME:READY:RESPONSE",
            body,
            service: {
              params: service.params,
              data: service.data,
            },
          })
          if (includeOlderJsonRpcCall) {
            send({
              jsonrpc: "2.0",
              id: id,
              method: "fcl:sign",
              params: [body, service.params],
            })
          }
        } catch (error) {
          throw error
        }
      },

      onResponse(e, {send, close}) {
        try {
          if (typeof e.data !== "object") return
          const resp = normalizePollingResponse(e.data)

          switch (resp.status) {
            case "APPROVED":
              resolve(resp.data)
              close()
              break

            case "DECLINED":
              reject(`Declined: ${resp.reason || "No reason supplied"}`)
              close()
              break

            default:
              reject(`Declined: No reason supplied`)
              close()
              break
          }
        } catch (error) {
          console.error("execIframeRPC onResponse error", error)
          throw error
        }
      },

      onMessage(e, {close}) {
        try {
          if (typeof e.data !== "object") return
          if (e.data.jsonrpc !== "2.0") return
          if (e.data.id !== id) return
          const resp = normalizePollingResponse(e.data.result)

          switch (resp.status) {
            case "APPROVED":
              resolve(resp.data)
              close()
              break

            case "DECLINED":
              reject(`Declined: ${resp.reason || "No reason supplied"}`)
              close()
              break

            default:
              reject(`Declined: No reason supplied`)
              close()
              break
          }
        } catch (error) {
          console.error("execIframeRPC onMessage error", error)
          throw error
        }
      },

      onClose() {
        reject(`Declined: Externally Halted`)
      },
    })
  })
}
